          gener_created = Genre.create(name: gener_name)
          MovieGenre.create(movie: movie_created, genre: gener_created)
        else
          MovieGenre.create(movie: movie_created, genre: gener_find)
          puts "gender already exist"
        end
      end
    end
    puts "created"
  end


